                    await habitTrackerservice.UpdateHabitOccasionsAsync(habit, startingdate);
                }

                UpdateUserActivities();
                habit = new Habit();
            }
            else
            {
                await habitTrackerservice.AddHabitAsync(habit, startingdate);
                UpdateUserActivities();
                habit = new Habit();
            }
            _ModalVisible = false;
            _edit = false;
